    file: string,
    dest?: string | undefined,
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    flags?: any
  ) => Promise<string>
  switch (osPlatform) {
    case 'linux': {
      // Use https://github.com/BtbN/FFmpeg-Builds builds
      release = await octo.rest.repos.getLatestRelease({
        owner: 'BtbN',
        repo: 'FFmpeg-Builds'
      })
