      // Use https://github.com/BtbN/FFmpeg-Builds builds
      release = await octo.rest.repos.getLatestRelease({
        owner: 'BtbN',
        repo: 'FFmpeg-Builds'
      })
      for (const asset of release.data.assets) {
        // ffmpeg-n5.1-latest-linux64-gpl-5.1.tar.xz
        if (
